mapper.select:
select Max(DEPTH)
from COMMENT
where GROUP_NUMBER = #{groupNumber}

mapper.update:
update COMMENT
set GROUP_REFORDER = GROUP_REFORDER + 1
where GROUP_NUMBER = #{groupNumber}
and GROUP_REFORDER > #{parentOrderResult}

update COMMENT
set CHILD_NUMBER = CHILD_NUMBER + 1
where COMMENT_ID = #{parentId}
