mapper.select:
select photo_id, photo_name, upload_date, photo_path, feed_id
from PHOTO
where FEED_ID = #{feedId}

select photo_id, photo_name, upload_date, photo_path, feed_id
from photo p
where p.feed_id = #{feedId};

mapper.update:
UPDATE PHOTO
set PHOTO_NAME = #{updateImageInfo.updateImageName},
PHOTO_PATH = #{updateImageInfo.updateImagePath}
where FEED_ID = #{feedId};
